        setForm(e.target.value)
    }

    useEffect(() => {

        const newTitle = {...eventDetail, title : form}
        console.log(newTitle)

        if(eventDetail){
            fetch(`http://localhost:4000/events/${eventDetail.id}`, {
            method: "PATCH",
            headers: {
            "Content-Type": "application/json"
        },
            body: JSON.stringify(newTitle)
        })
        }
    },[form])

    return (
        <div>
            <h3>My Event</h3>
            <h4> Title:
                <textarea id='title-textarea' onChange = {handleChange}>{eventDetail.title}
                </textarea></h4>
            <h4>Starts: {eventDetail.start.toString()}
            <DateTimePicker /></h4>
            <h4>Ends: {eventDetail.end.toString()}
            <DateTimePicker /></h4>

        </div>
    )
}

export default PopupContent
