            <div className='popup-inner'>
                <button id='close-btn-id' className='close-btn' onClick={() => setTrigger(false)}>‚ùå</button>
                <PopupContent key={clickEvent.id} 
                eventDetail = {clickEvent} getData = {getData}/>
                <button id='remove-btn-id' onClick={() => handleRemove(clickEvent)}>üóë</button>
            </div>
        </div>)
        : "";
}

export default Popup
